import logo from './logo.svg';
import './App.css';
import React from 'react';
import { ReactDOM } from 'react';
import { render } from 'react-dom';

function App () {
  return(
    <div className="App">
      <div className="App-header">
         Marketplace
      </div>
      <div className="App-body">
        <div className="App-header-2">
          <p/>
          Your household
          <p/>
        </div>
        Welcome to The Marketplace! Review your household below:
        <p/>
        <Dashboard/>
      </div>
    </div>
  );
}

class Dashboard extends React.Component {
  state = {
    members: [
      {
        name: "Bud Baxter",
        description: "Household contact",
        favFruit: "Apple"
      },
      {
        name: "Andy Baxter",
        description: "Household contact",
        favFruit: "Apple"
      },
    ]
  }

  handleNewMember = (nName, nDescription, nFavFruit) => {
    this.addMember(nName, nDescription, nFavFruit);
  }

  addMember = (nName, nDescription, nFavFruit) => {
    const m = {
      name: nName,
      description: nDescription,
      favFruit: nFavFruit
    }

    this.setState({
      members: this.state.members.concat(m),
    })
  }

  render(){
    return(
    <div className="App">
      <MemberList
        members={this.state.members}
      />
      <p/>
      <ToggleNewMember
        handleNewMember={this.handleNewMember}
      />
    </div>
    );
  }
}

class MemberList extends React.Component {
  render(){
    return(
      <div className="container">
        {
          this.props.members.map((member) => {
            return(
              <MemberEntry
                name={member.name}
                description={member.description}
                favFruit={member.favFruit}
              />
          )})
        }
      </div>
    );
  }
}

class MemberEntry extends React.Component {
  render(){
    return(
      <div className="card-container">
      <div className="card">
        {this.props.name}; {this.props.description}; {this.props.favFruit}
      </div>
      </div>
    );
  }
}

class ToggleNewMember extends React.Component {
  state = {
    isOpen: false,
  }

  toggleForm = () => {
    this.setState({
      isOpen: !this.state.isOpen,
    })
  }

  render(){
    if(this.state.isOpen){
      return(
        <NewMemberForm
          toggleForm={this.toggleForm}
          handleNewMember={this.props.handleNewMember}
        />
      )
    } else {
      return(
        <button className="button" onClick={() => {this.toggleForm()}}>Add new member</button>
      )
    }
  }
}

class NewMemberForm extends React.Component {
  state = {
    name: "",
    description: "",
    favFruit: "",
  }

  handleSubmit = () => {
    this.props.handleNewMember(this.state.name, this.state.description, this.state.favFruit);
    this.props.toggleForm();
  }

  handleNameChange = (e) => {
    this.setState({ name: e.target.value });
  }

  handleDescriptionChange = (e) => {
    this.setState({ description: e.target.value });
  }

  handleFruitChange = (e) => {
    let fruit = e.target.value;
    console.log("Fruit: ", fruit)
    this.setState({ favFruit: fruit }, () => {
      console.log("NewFruit: ", this.state.favFruit)
    });

  }

  render(){
    return(
      <div>
        <form>
          <label>Name</label>
          <input 
            type="text" 
            value={this.state.name}
            onChange={this.handleNameChange}
          />
          <label>Description</label>
          <input
            type="text"
            value={this.state.description}
            onChange={this.handleDescriptionChange}
          />
          <label>Favorite Fruit</label>
          <select id="fruit" onChange={this.handleFruitChange}>
            <option value="Apple">Apple</option>
            <option value="Banana">Banana</option>
            <option value="Orange">Orange</option>
            <option value="Mango">Mango</option>
            <option value="Kiwi">Kiwi</option>
            <option value="Durian">Durian</option>
          </select>

        <button className="button">
          Submit
        </button>
        <button className="button" onClick={() => {this.props.toggleForm()}}>
          Cancel
        </button>
        </form>
      </div>
    )
  }
}

export default App;
