import logo from './logo.svg';
import './App.css';
import React from 'react';
import { ReactDOM } from 'react';
import { render } from 'react-dom';

function App () {
  return(
    <Dashboard/>
  );
}

class Dashboard extends React.Component {
  state = {
    members: [
      {
        name: "Bud Baxter",
        description: "Household contact",
        favFruit: "Apple"
      },
      {
        name: "Andy Baxter",
        description: "Household contact",
        favFruit: "Apple"
      },
    ]
  }

  render(){
    return(
    <div className="App">
      <MemberEntry
        name={this.state.members[0].name}
        description={this.state.members[0].description}
        favFruit={this.state.members[0].favFruit}
      />
    </div>
    );
  }
}

class MemberList extends React.Component {
  render(){
    return(memberMapping(this.props.members))
/*    const members = this.props.members.map((member) => {
      return(
      <MemberEntry
        name={member.name}
        description={member.description}
        favFruit={member.favFruit}
      />
    )});*/
  }
}

function memberMapping(members) {
  console.log(members)
  members.map((member) => {
    return(
      <MemberEntry
        name={member.name}
        description={member.description}
        favFruit={member.favFruit}
      />
    )
  })
}

class MemberEntry extends React.Component {
  render(){
    return(
      <div className="header">
        {this.props.name}; {this.props.description}; {this.props.favFruit}
      </div>
    );
  }
}

export default App;
