import logo from './logo.svg';
import './App.css';
import React from 'react';
import { ReactDOM } from 'react';
import { render } from 'react-dom';

function App () {
  return(
    <div className="App">
      <div className="App-header">
         Marketplace
      </div>
      <div className="App-body">
        <div className="App-header-2">
          <p/>
          Your household
        </div>
        Welcome to The Marketplace! Review your household below:
        <Dashboard/>
      </div>
    </div>
  );
}

class Dashboard extends React.Component {
  state = {
    members: [
      {
        name: "Bud Baxter",
        description: "Household contact",
        favFruit: "Apple"
      },
      {
        name: "Andy Baxter",
        description: "Household contact",
        favFruit: "Apple"
      },
    ]
  }

  render(){
    console.log("Members: ", this.state.members)
    return(
    <div className="App">
      <MemberList
        members={this.state.members}
      />
      <ToggleNewMember/>
    </div>
    );
  }
}

class MemberList extends React.Component {
  render(){
    console.log("prop members");
    return(
      <div className="container">
        {
          this.props.members.map((member) => {
            return(
              <MemberEntry
                name={member.name}
                description={member.description}
                favFruit={member.favFruit}
              />
          )})
        }
      </div>
    );
  }
}

class MemberEntry extends React.Component {
  render(){
    return(
      <div className="card">
        {this.props.name}; {this.props.description}; {this.props.favFruit}
      </div>
    );
  }
}

class ToggleNewMember extends React.Component {
  state = {
    isOpen: false,
  }

  toggleForm = () => {
    this.setState({
      isOpen: !this.state.isOpen,
    })
  }

  render(){
    if(this.state.isOpen){
      return(
        <NewMemberForm
          toggleForm={this.toggleForm}
        />
      )
    } else {
      return(
        <button onClick={() => {this.toggleForm()}}>Add new member</button>
      )
    }
  }
}

class NewMemberForm extends React.Component {
  state = {
    name: "",
    description: "",
    favFruit: "",
  }
  render(){
    return(
      <div>
        <div>
          DID IT
        </div>
        <button onClick={() => {this.props.toggleForm()}}>
          Cancel
        </button>
        <button>
          Submit
        </button>
      </div>
    )
  }
}

export default App;
